// MainContent.js
import React, { useState, useEffect } from "react";
import styled from "styled-components";
import MovieSlider from "./MovieSlider"; // Import the MovieSlider component
import Loading from "./Loading"; // Import the Loading component

// Replace placeholder image paths with real image URLs
const realSlides = [
  { id: 1, imageUrl: "path_to_your_image_1.jpg" },
  { id: 2, imageUrl: "path_to_your_image_2.jpg" },
  { id: 3, imageUrl: "path_to_your_image_3.jpg" },
  // Add more slides with real image URLs
];

const MainContentContainer = styled.div`
  position: relative;
  width: 100vw;
  height: 100vh;
`;

const MainContent = ({ isLoading }) => {
  const [loading, setLoading] = useState(isLoading);

  useEffect(() => {
    setLoading(isLoading);
  }, [isLoading]);

  return (
    <MainContentContainer>
      {loading ? <Loading /> : <MovieSlider slides={realSlides} />}
    </MainContentContainer>
  );
};

export default MainContent;
