import React, { useRef, useState } from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import styles from "./MovieSlider.module.scss";

const MovieSlider = ({ slides }) => {
  const sliderRef = useRef(null);
  const [currentSlide, setCurrentSlide] = useState(0);
  const [lastScrollTime, setLastScrollTime] = useState(0);

  const settings = {
    dots: false,
    infinite: true,
    speed: 1000,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: false,
    autoplaySpeed: 2000,
    swipe: false, // Disable swipe to slide
    touchMove: false, // Disable touch move
    arrows: false, // Remove arrow buttons
    draggable: false, // Disable dragging
    beforeChange: (oldIndex, newIndex) => setCurrentSlide(newIndex),
  };

  // Function to handle scroll events
  const handleScroll = (e) => {
    // Limit scroll events to once every 500ms
    if (Date.now() - lastScrollTime < 500) {
      return;
    }

    setLastScrollTime(Date.now());

    if (e.deltaY > 0) {
      sliderRef.current.slickNext(); // Go to the next slide on scroll down
    } else if (e.deltaY < 0) {
      sliderRef.current.slickPrev(); // Go to the previous slide on scroll up
    }
  };

  return (
    <div
      className={styles.sliderContainer}
      onWheel={(e) => {
        e.preventDefault(); // Prevent default wheel scrolling behavior
        handleScroll(e); // Handle custom slide navigation on wheel
      }}
    >
      <Slider
        {...settings}
        initialSlide={currentSlide}
        ref={sliderRef}
        className={styles.touchpadSlider} // Add custom class for touchpad-only styles
        fade={true} // Enable fade effect
        cssEase="linear" // Set linear easing for smooth transitions
      >
        {slides.map((slide) => (
          <div key={slide.id}>
            <img
              src={slide.imageUrl}
              alt={`Slide ${slide.id}`}
              className={styles.slide}
              draggable="false" // Disable image dragging
            />
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default MovieSlider;
