import React, { useRef, useState } from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import styles from "./MovieSlider.module.scss";

const MovieSlider = ({ slides, bannerContent, onSlideChange }) => {
  const sliderRef = useRef(null);
  const [currentSlide, setCurrentSlide] = useState(0);
  const [lastScrollTime, setLastScrollTime] = useState(0);

  const settings = {
    dots: false,
    infinite: true,
    speed: 1000,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: false,
    autoplaySpeed: 2000,
    swipe: false,
    touchMove: false,
    arrows: false,
    draggable: false,
    beforeChange: (oldIndex, newIndex) => {
      setCurrentSlide(newIndex);
      onSlideChange(newIndex);
    },
  };

  const handleScroll = (e) => {
    if (Date.now() - lastScrollTime < 2500) {
      return;
    }

    setLastScrollTime(Date.now());

    if (e.deltaY > 0) {
      sliderRef.current.slickNext();
    } else if (e.deltaY < 0) {
      sliderRef.current.slickPrev();
    }
  };

  return (
    <div
      className={styles.sliderContainer}
      onWheel={handleScroll}
      onTouchStart={(e) => e.preventDefault()}
      onTouchMove={(e) => e.preventDefault()}
      onTouchEnd={(e) => e.preventDefault()}
      onTouchCancel={(e) => e.preventDefault()}
    >
      <Slider
        {...settings}
        initialSlide={currentSlide}
        ref={sliderRef}
        className={styles.touchpadSlider}
        fade={true}
        cssEase="linear"
      >
        {slides.map((slide, index) => (
          <div key={slide.id}>
            <div
              className={`${styles.slideContent} ${
                styles[`banner${index + 1}`]
              }`}
            >
              <img
                src={slide.imageUrl}
                alt={`Slide ${slide.id}`}
                className={styles.slideImage}
                draggable="false"
              />
              <div className={styles.bannerContent}>
                <h2>{bannerContent.title}</h2>
                <p className={styles.span}>{bannerContent.text}</p>
                <p className={styles.content}>{bannerContent.content}</p>
                <a href={bannerContent.buttonLink}>
                  {bannerContent.buttonLabel}
                </a>
              </div>
            </div>
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default MovieSlider;
