// MainContent.js
import React, { useState, useEffect } from "react";
import styled from "styled-components";
import MovieSlider from "./MovieSlider"; // Import the MovieSlider component
import Loading from "./Loading"; // Import the Loading component

// Replace placeholder image paths with real image URLs
const realSlides = [
  { id: 1, imageUrl: "/images/img.pdf" },
  { id: 2, imageUrl: "/images/img.pdf" },
  { id: 3, imageUrl: "p/images/img.pdf" },
  // Add more slides with real image URLs
];

const MainContentContainer = styled.div`
  position: relative;
  width: 100vw;
  height: 100vh;
`;

const MainContent = () => {
  const [isLoading, setIsLoading] = useState(true);

  // Simulate loading delay with setTimeout
  useEffect(() => {
    const timer = setTimeout(() => {
      setIsLoading(false);
    }, 2000); // Simulate 2 seconds loading time
    return () => clearTimeout(timer);
  }, []);

  return (
    <MainContentContainer>
      {isLoading ? <Loading /> : <MovieSlider slides={realSlides} />}
    </MainContentContainer>
  );
};

export default MainContent;
