// MovieSlider.js
import React from "react";
import styled from "styled-components";

const SliderContainer = styled.div`
  width: 100%;
  height: 100%;
  overflow: hidden;
`;

const Slide = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
`;

const MovieSlider = ({ slides }) => {
  return (
    <SliderContainer>
      {slides.map((slide) => (
        <Slide key={slide.id} src={slide.imageUrl} alt={`Slide ${slide.id}`} />
      ))}
    </SliderContainer>
  );
};

export default MovieSlider;
