import React, { useRef, useState } from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import styles from "./MovieSlider.module.scss";

const MovieSlider = ({ slides }) => {
  const sliderRef = useRef(null);
  const [currentSlide, setCurrentSlide] = useState(0);

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 2000,
    swipe: false, // Disable swipe to slide
    beforeChange: (oldIndex, newIndex) => setCurrentSlide(newIndex),
  };

  // Function to handle mousewheel event
  // Function to handle mousewheel event
  const handleWheel = (event) => {
    // Check if the mouse wheel event occurred within the slider container
    if (sliderRef.current && sliderRef.current.contains(event.target)) {
      // Calculate the deltaY of the mouse wheel event
      const deltaY = event.deltaY;

      // If deltaY is positive, scroll down; if deltaY is negative, scroll up
      if (deltaY > 0) {
        setCurrentSlide((prevSlide) => prevSlide + 1); // Go to the next slide
      } else if (deltaY < 0) {
        setCurrentSlide((prevSlide) => prevSlide - 1); // Go to the previous slide
      }

      // Prevent default behavior of scrolling the page
      event.preventDefault();
    }
  };

  return (
    <div
      className={styles.sliderContainer}
      ref={sliderRef}
      onWheel={handleWheel} // Attach the handleWheel function to the onWheel event of the slider container
    >
      <Slider {...settings} initialSlide={currentSlide}>
        {slides.map((slide) => (
          <div key={slide.id}>
            <img
              src={slide.imageUrl}
              alt={`Slide ${slide.id}`}
              className={styles.slide}
            />
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default MovieSlider;
``;
