// MovieSlider.js
import React from "react";
import styles from "./MovieSlider.module.scss";

const MovieSlider = ({ slides }) => {
  return (
    <div className={styles.sliderContainer}>
      {slides.map((slide) => (
        <img
          key={slide.id}
          src={slide.imageUrl}
          alt={`Slide ${slide.id}`}
          className={styles.slide}
        />
      ))}
    </div>
  );
};

export default MovieSlider;
